;; **
;;; ## PDFA object
;; **

;; @@
(defm new-pdfa [alphabet K transition emissiondist]
  (let [alphabet (map str (seq alphabet))

        states (range K)

        edges (into {} (map (fn [state]
                              [state (into {} (map #(vector % (transition state %))
                                                   alphabet))])
                            states))

        probs (into {} (map (fn [state]
                              [state (into {} (map (fn [[symbol prob]]
                                                     [(str symbol) prob])
                                                   (:categories (emissiondist state))))])
                            states))]
    {:edges edges
     :probs probs}))
;; @@
;; =>
;;; {"type":"html","content":"<span class='clj-var'>#&#x27;pdfa/new-pdfa</span>","value":"#'pdfa/new-pdfa"}
;; <=

;; @@
(defn pdfa-states [pdfa]
  (keys (:edges pdfa)))

(defn pdfa-edges [pdfa]
  (let [t (:edges pdfa)]
    (into {} (map (fn [state]
                    [state (vec (set (vals (get t state))))])
                  (keys t)))))
;; @@
;; =>
;;; {"type":"html","content":"<span class='clj-var'>#&#x27;pdfa/pdfa-edges</span>","value":"#'pdfa/pdfa-edges"}
;; <=

;; @@
(defn pdfa-node->descriptor [pdfa]
   (fn [node]
     {:label (str node)}))

(defn pdfa-edge->descriptor [pdfa]
  (fn [state nextstate]
    (let [edges (get-in pdfa [:edges state])
          probs (get-in pdfa [:probs state])
          
          emissions (filter #(= nextstate (get edges %))
                            (keys edges))
          probs (into {}
                      (map #(vector % (get probs %))
                           emissions))]
      {:label (clojure.string/join "|" (map #(format "%.3f%s"(get probs %) %)
                                            emissions))})))
;; @@
;; =>
;;; {"type":"html","content":"<span class='clj-var'>#&#x27;pdfa/pdfa-edge-&gt;descriptor</span>","value":"#'pdfa/pdfa-edge->descriptor"}
;; <=

;; @@
(defn view-pdfa [pdfa]
  (rhizome.viz/view-graph (pdfa-states pdfa)
                          (pdfa-edges pdfa)
                          :node->descriptor (pdfa-node->descriptor pdfa)
                          :edge->descriptor (pdfa-edge->descriptor pdfa)))
;; @@
;; =>
;;; {"type":"html","content":"<span class='clj-var'>#&#x27;pdfa/view-pdfa</span>","value":"#'pdfa/view-pdfa"}
