(def alphabet "ab")
(def alphabet-len (count alphabet))
(defn one-hot [len hot]
  (assoc (vec (replicate len 0)) hot 1))
(defn encode-char [s]
  (let [i (.indexOf alphabet s)]
    (one-hot alphabet-len i)))
(defn encode-string [s]
  (vec (map #(encode-char (str %)) s)))

