;; **
;;; ## Inference
;; **

;; @@
(def test-observations
  (combine-observes-fn (sample-observes-from-prior pdfa [(repeat 100 nil) alphabet])))


(defn indices [pred coll]
   (keep-indexed #(when (pred %2) %1) coll))

(defn first-index [pred coll]
  (first (indices pred coll)))

(first-index #(= % 1) [0 0 1])

(def test-observations-str
  (apply str (mapv #(nth alphabet (first-index (fn [x] (= x 1)) %)) test-observations)))
